package com.example.ToolRent.dto;

import lombok.AllArgsConstructor;
import lombok.Data;
import lombok.NoArgsConstructor;
import java.time.LocalDate;

@Data
@AllArgsConstructor
@NoArgsConstructor
public class ActiveLoanReportDTO {
    private Long loanId;
    private String customerName;
    private String customerRut;
    private String toolName;
    private String toolCategory;
    private LocalDate loanDate;
    private LocalDate returnDate;
    private String status; // "activo" (vigente) o "vencido" (atrasado)
    private int loanValue;
    private Long daysOverdue; // null si está vigente, >0 si está atrasado
}
